import { useNavigate } from "react-router-dom";
import { useState , useEffect } from "react"
import axios from "axios";

function Register() {

    const navigate = useNavigate();


    const [email,setEmail] = useState('')
    const [password,setPassword] = useEffect('')

    async function submit(e){
        e.preventDefault()

        try{
            await axios.post("http://localhost:5000/login",{
                email,
                password
            })
        }
        catch(e){
            console.log(e)
        }
    }

    return(
        <div className="Login-container">
            <h1>New user registration page</h1>
            <form action="POST">

                <input type="text" placeholder="Enter Name.."  className="login-input" />
                <input type="text" placeholder="Enter Email.."  className="login-input" />
                <input type="text" placeholder="Enter Username.."  className="login-input" />
                <input type="password" placeholder="Enter Password.."  className="login-input" />
                <button className="login-submit-button" >Submit</button>

            </form>
            <button onClick={() => navigate("/")} className="login-home-button">Home</button>
        </div>

    )
}


export default Register
