import { useNavigate } from "react-router-dom";
import axios from "axios";

function Register() {
    const navigate = useNavigate();

    const [user,setUser] = useState({
        name:"",
        email:"",
        username: "",
        password: ""
    })
    const handleChange = e =>{
        const {name,value} = e.target
        setUser({
        ...user,//spread operator 
        [name]:value
        })
        }

    //register function 
   const egister = ()=>{
    const {name,email,password} = user
    if (name && email && password){
     axios.post("http://localhost:5000/Register",user )
     .then(res=>console.log(res))
    }
    else{
        alert("invalid input")
    };












    return(
        <div className="Login-container">
            <h1>New user registration page</h1>
            <form>

                <input type="text" placeholder="Enter Name.."  className="login-input" value={user.name} onChange={handleChange}/>
                <input type="text" placeholder="Enter Email.."  className="login-input" value={user.email} onChange={handleChange}/>
                <input type="text" placeholder="Enter Username.."  className="login-input" value={user.username} onChange={handleChange}/>
                <input type="password" placeholder="Enter Password.."  className="login-input" value={user.password} onChange={handleChange}/>
                <button className="login-submit-button" onClick={egister}>Submit</button>

            </form>
            <button onClick={() => navigate("/")} className="login-home-button">Home</button>
        </div>

    )
}
}

export default Register
