import { useState , useEffect } from 'react'
import { useNavigate  , Link } from "react-router-dom";
import Register from './Register';
import axios from 'axios';
import { CookiesProvider, useCookies } from "react-cookie";

function Login() {
    const navigate = useNavigate();

    // const history = useNavigate()

    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')
    const [cookies, setCookies] = useCookies({ });


    async function submit(e){
        e.preventDefault()

        try{
            await axios.post("http://localhost:5000/login",{
                email,
                password
            })
            .then(res => {
                if(res.data==="exist"){
                    navigate("/account" )
                    const passing = email

                    alert("Welcome" + cookies.Email)
                   console.log(cookies)
                   const updateCookie = {
                    Email:email,

                   }
                   setCookies(updateCookie);

                }
                else if(res.data==="Invalid Credentials") {
                    alert(res.data)
                }
            }).catch(e=>{
                alert("Wrong details")
                console.log(e)
            })
        }
        catch(e){
            console.log(e)
        }




    }



        return(
            <div className="Login-container">
                <h1>This is login page</h1>
                <form action="POST">
                    <input type="text" placeholder="Enter Email" className="login-input" onChange={(e) => {setEmail(e.target.value)}}/>
                    <input type="password" placeholder="Enter Password" className="login-input" onChange={(e) => {setPassword(e.target.value)
                    setCookies('Email', email, { path: '/' })
                    setCookies('Password', password , { path: '/' })
                    }}/>
                    <button className="login-submit-button" onClick={submit}>Submit</button>
                </form>
                <button onClick={() => {navigate("/");}}
                    // setCookies('Email', email, { path: '/' });
                     className="login-home-button">Home</button>

                <br/>
                <br/>

                <p>OR</p>

                <br/><br/>
                <Link to="/register" >Register</Link>


            </div>
        )



}

export default Login
